function waitFor(test, complete, timeout) {
  var result, start = new Date().getTime()
  setInterval(function interval() {
    if ((new Date().getTime() - start < timeout) && !result) {
      result = test()
    } else {
      if (!result) {
        phantom.exit(1)
      } else {
        complete()
        clearInterval(interval)
      }
    }
  }, 100)
}


